import { FaCaretUp,FaSortDown } from "react-icons/fa";
import Towns from './../../pages/towns/towns';
import City from './../citys/city';
import MapboxGeocoder from '@mapbox/mapbox-gl-geocoder'
var mapboxgl = require('mapbox-gl/dist/mapbox-gl.js');
var MapboxLanguage = require('@mapbox/mapbox-gl-language');
// import mapboxgl from 'mapbox-gl'
const Customerdetail=(props)=>{
    useEffect(() => {
       
        mapboxgl.accessToken = 'pk.eyJ1IjoiYmVobmFtMTciLCJhIjoiY2ticzRoNWNjMDAyZTJ5bDU5OWpiZ3B3diJ9.BDs34GTjlq6M3trXe40xvQ';
        var map = new mapboxgl.Map({
        container: 'map',
        style: 'mapbox://styles/mapbox/streets-v11'
        });
 
     mapboxgl.setRTLTextPlugin('https://api.mapbox.com/mapbox-gl-js/plugins/mapbox-gl-rtl-text/v0.10.1/mapbox-gl-rtl-text.js', 
     true );
var language = new MapboxLanguage({defaultLanguage: 'ur'});
map.addControl(language);

map.addControl(new MapboxGeocoder({       accessToken: mapboxgl.accessToken   }));
map.addControl(
    new mapboxgl.GeolocateControl({
    positionOptions: {
    enableHighAccuracy: true
    },
    trackUserLocation: true
    })
    );
    }, [])
    const {customertowns,customercity,showtown,towns,showcity,citys}=useContext(mainContext)
   
    const [drop,setdrop]=useState('Dropdown');
    // const[showtown,setshowtown]=useState(false);
   const nametown=(text)=>{
    towns(text)
   }
   const namecity=(text)=>{
    citys(text)
   }
   
    return(
       <div id="map">     
           
       </div>
    )
}
export default Customerdetail;
