import React, { Component,useContext,useEffect,useState } from 'react'
import axios from 'axios';
import {mainContext} from './../../../context/mainContext';
import { IconContext } from "react-icons";
import {AiOutlineDelete } from "react-icons/ai";
import './buys.css';
const Buys=()=>{
    const {userid,login}=useContext(mainContext)
    const[buydata,setbuydata]=useState([]);
    console.log(buydata,'xxx')
    const id=localStorage.getItem('user')
    // console.log(userid,'gghjjh')
    // console.log(id,'kkk')
    useEffect(()=>{
        axios.get(`http://localhost:3000/user/buys/${id}`, { 
        })
        .then((response)=>{
            // setdata(response.data);
            setbuydata(response.data)
            // console.log(response.data,'hhh')
        })
    },[]);
    return(
        <div className="mainbuys">
            {buydata.map((data)=>
          <div className="buydetail">
              <div className="mainimagebuy">
          <img src="https://www.beytoote.com/images/stories/housekeeping/hou16389.jpg" className="buysImage" />
          </div>
          <div className="buystitle">
          <text className="buytitletext"> <span style={{color:'rgb(6, 73, 17)'}}>نام کالا:</span>{data.title}</text>
            <text className="buytitletext">رنگ:{data.color}</text>
          </div>
          <div className="buyprice">
              <text>قیمت:</text>
          <text ><span style={{color:'red',fontWeight:'bold'}}>{data.prices}</span> تومان</text>
          <div className="buynumber">
          <text>تعداد:</text>
           <input  defaultValue={data.Count}  className="inputnumberbuy"/>
           <IconContext.Provider
            value={{ color: 'red', size: '25px'}}>
       <AiOutlineDelete  /> 
          </IconContext.Provider>
          </div>
          </div>
         
          
          </div>
          )}
        </div>
    )
}
export default Buys;
