import React, {useState } from 'react';
import './changepassword.css';
import ReCAPTCHA from "react-google-recaptcha";
import { Link } from 'react-router-dom';

const Changepassword=(props)=>{
    const [username, setusername] = useState('');
    const [password, setpassword] = useState('');
    const [errors, seterrors] = useState('');
    const [showerror,setshowerror]=useState('none')
    const [recaptcha,setRecaptcha]=useState('');
    const mail=(event)=>{
        setusername(event.target.value)
    }
    const pass=(event)=>{
        setpassword(event.target.value)
    }
    const recaptchastate=()=>{
      setRecaptcha(true)
     }
     
    const changepass=()=>{
if(username.indexOf('.com')==-1 ||username.indexOf('@')==-1 ){
    seterrors('فرمت ایمیل نامعتبر است')
    setshowerror('flex')
}else if(password.length < 5){
    seterrors('تعدادارقام پسوورد نباید کمتر از 5باشد')
    setshowerror('flex')
}else{
    return(
        setshowerror('none'),
        fetch('http://localhost:3000/auth/password/reset',{
                 method:'POST',
                 headers:{
                   'Accept':'application/json',
                   'Content-Type': 'application/x-www-form-urlencoded'
                 },
                 body:JSON.stringify(
                 {
                     email:username,
                     password:password
            
                   }
                  )
               })  
                .then(async (response)=>{
                 const data = await response.json();
                 const statuse=await response.status;
               console.log(data)
               if(statuse == 200){
              setshowerror('none')
             gohome()
            
               }else if(statuse == 400){
                seterrors(data.message)
                setshowerror('flex')
               }
               }
              
              )
               .catch((error)=>{console.error(error)})
               )
            }
    }
    return(
        <div className="mainreset">
<div className="error" style={{display:errors.length<1?'none':'',}}>
{errors}
</div>
     <input type="email" placeholder="ایمیل" onChange={mail} />
     <input type="password" placeholder="پسوورد" onChange={pass} />
    <div className="recaptcha">
    {/* <ReCAPTCHA
    hl={"fa"}
    sitekey="6LcdfOUUAAAAAFGXuwyP37TpZSyQpHHGxE28WGYo"
    onChange={recaptchastate}
  /> */}
 </div>
<button onClick={changepass} >
بازیابی رمز
</button>
        </div>
    )
}
export default Changepassword;
