      <div  className="createpost">

     
       <h1>ایجاد پست جدید</h1>
  
       <input type="text" placeholder="عنوان پست را وارد کنید" className="inputs" onChange={title} name="title"/>
       <input type="text" placeholder="قیمت را وارد کنید" className="inputs" onChange={price} name="price"  />
       <textarea  type="text" placeholder="توضیحات "  className="inputs" onChange={explain} name="explain" >

