import React, { Component ,useState,useEffect} from 'react';
import { IconContext } from "react-icons";
import { GoSearch  } from "react-icons/go";
import './searchbar.css';
const Searchbar=()=>{
    const[opacittytext,Setopacittytext]=useState(0.7)
   const[sizetext,Setsizetext]=useState(35)
    useEffect(() => {
      let interval = setInterval(() => {

            if(opacittytext==0.7 && sizetext==35){
                Setopacittytext(1)

            }else if(opacittytext==1){
                Setopacittytext(0.7)
            }
          
        }, 100);
    
        return () => {
          clearInterval(interval);
        };
      });
      
    return(
<div style={{marginTop:20,display:'flex'}}>
 
    <div className="searchBartext">
 
    <text style={{opacity:opacittytext}} className="textstyle">Beh Kala</text>
    </div>

     <input type="text" className="input" placeholder="جستجوی محصولات"/> 

<div className="icon">
<IconContext.Provider

      value={{ color: '#F79F1F', size: '30px'}}>
 <GoSearch /> 
    </IconContext.Provider>
    </div>



   
   
 
     </div>
    )
}
 export default Searchbar;
