import React, { Component,useState } from 'react';
import './Modal.css';
import Backdrop from './../backdrop/backdrop';
import {Link} from 'react-router-dom';
const Modal=(props)=>{
    const [username, setusername] = useState('');
    const [password, setpassword] = useState('');
    const [errors, seterrors] = useState('');
    const login=()=>{
        return(

            fetch('http://localhost:3000/auth/login',{
                
                  
                     method:'POST',
                     headers:{
                       'Accept':'application/json',
                       'Content-Type': 'application/x-www-form-urlencoded'
                     },
                     body:JSON.stringify(
                     {
                       
                         email:'jjh',
                          password:password 
                       }
                      )
                   })   .then(async (response)=>{
                     const data = await response.json();
                     const statuse=await response;
                   if(statuse ==200){
                  alert('ok')
                   }else{
                    console.log('no')
                   }
                   }
                  
                  )
                   .catch((error)=>{console.error(error)})
                   )
    }
    return(
        <React.Fragment>
            <Backdrop show={props.showModal} modalclose={props.closemodal}/>
      <div className="Modal"
        style={{transform:props.showModal?'translateX(0)':'translateX(-100vw)',
    opacity:props.showModal?'1':'0'
    }}
        >
 
         <input type="text" placeholder="username"  />
    <input type="password" placeholder="password" onChange={(text)=>setpassword(text)} />
    <div style={{display:'bluck',marginBottom:30,marginTop:15}}>
    <button onClick={login} style={{marginRight:30}}>
        ورود
    </button>
    <a onClick={()=>alert('kkk')} className="apassword">
       رمز عبور خود را فراموش کرده ام
    </a>
    </div>
   
    <span style={{marginRight:15,color:'white'}}>کاربر جدید هستید؟
    <Link style={{color:'#F79F1F'}} onClick={props.closemodal}  to="/register" >ثبت نام</Link> کنید </span>
    </div>
 

        </React.Fragment>

  
    )
}
export default Modal;
