import React, { Component } from 'react';
import {  IoIosArrowForward } from "react-icons/io";
import { IconContext } from "react-icons";
import {Link} from 'react-router-dom';
import './sideDrawer.css';
import Logo from './../logo/logo';
 import Backdrop from '../backdrop/backdrop';
const SideDrawer=(props)=>{
   const changeDrawer=()=>{
   props.closeDrawer()
   }
    let classes=['SideDrawer','Close'];
    if(props.show){
        classes=['SideDrawer','open']
    }
    return(
        <React.Fragment>
 <Backdrop show={props.show} modalclose={props.closeDrawer}/>
<div className={classes.join(' ')} style={{paddingTop:0}} >
    <div>
    <Logo/> 
    </div>
    
  
    <div > 
    <Link className="Draweritems" onClick={changeDrawer} to="/">صفحه اصلی</Link>
       <Link className="Draweritems" onClick={changeDrawer} to="/flower">گل</Link>
       <Link className="Draweritems" >لوتزم خانگی</Link>
       <Link className="Draweritems" >آرایشی و بهداشتی</Link>
       <Link className="Draweritems"  onClick={changeDrawer} to="/Computer" > کامپیوتر و لپ تاپ</Link>
       <Link className="Draweritems" >لوازم جانبی دیجیتال</Link>
      
   </div>
   </div>
   
        </React.Fragment>
        
    )
}
export default SideDrawer;
