import React, { Component,useEffect,useContext,useState } from 'react'
import Login from '../login/login';
import {mainContext} from './../../../context/mainContext';
import {Stufflistcontext} from './../../../context/stufflistcontext';
import Slideshow from '../../../components/slideshow/slideshow';
import axios from 'axios';
import Spiner from './../../spinner/spinner'
import {Link} from 'react-router-dom';
import Searchbar from './../../searchbar/searchbar';
import Dropdown3 from './../../Dropdown/Dropdown3/Dropdown3'
import './Homepage.css';
const HomePage=()=>{
    const {login,username,admin}=useContext(mainContext)
    const {modal,changemodal,searchbar}=useContext(Stufflistcontext)
    const [showspiner,setShowspiner]=useState(false);
    const [data, setdata] = useState([])
    useEffect(()=>{
        axios.get('http://localhost:3000/all', { 
      
        }) .then((response)=>{
            setShowspiner(false)
            console.log(response,'hhh')
            if(response.status==200){
                setdata(response.data)
            }else if(response.status==400){
                setShowspiner(false)
                alert('خطایی رخ داد')
            }else{
                setShowspiner(false)
                alert('خطا در ارتباط با سرور')
            }  
        }
        )  
    })
    return(
        <div>   
                    <div className="searchBar"> 
                    <div className="mainsearch" >
 
 <div className="searchBartext">

 <text style={{color:textcolor}} className="textstyle">Beh Kala</text>
 </div>

  

<div className="search">
<div className="maininputicon">
<input style={{backgroundColor:'white',borderTopRightRadius:'0px',borderBottomRightRadius:'0px',borderRight:'none'}} type="text" className="searchinput" placeholder="جستجوی محصولات"/> 
<div className="mainiconsearch">
<div className="iconsearch">
<IconContext.Provider

   value={{ color: '#F79F1F', size: '30px'}}>
<GoSearch /> 
 </IconContext.Provider>
 </div>
 </div>
 </div>
 </div>
</div>
</div>
<div style={{opacity:modal?'0':'1',zIndex:1}}  className="dropdownmenu">
    <Dropdown3 /> 
    </div>
        <div style={{display:'flex',justifyContent:'center',alignItems:'center'}}>
            
             { <Slideshow /> }
       
        </div>
 
        
    <div style={{display:showspiner?'flex':'none'}}>
    <Spiner/>
    </div>
    {/* <div className="searchbar"> 
<Searchbar />
</div>  */}
    <div className="maincard">
    
    
    {data.map((dataa)=>
        <div className="flowercards">
        <Link to={{
            pathname: '/postdetails',
            state: {
                id:dataa._id
            }
        }}>
          <div className="mainstuffcard">
        <img src="https://www.beytoote.com/images/stories/housekeeping/hou16389.jpg" className="flowerImage" />
        <div style={{display:'flex',alignItems:'center',flexDirection:'column'}}>
        <text >{dataa.title}</text>
        <div className="flowerprice">
        <text style={{color:'#009432'}}>تومان</text>
        <text style={{color:'#009432'}}>{dataa.prices}</text>
        </div> 
        </div> 
        </div>
        </Link>
      
        
        </div>
        
        )
    }
    </div>
   
        </div>
    )
}
export default HomePage;
