const [data, setdata] = useState([]);

const deletok=()=>{
    alert('حذف با موفقیت انجام شد')
    window.location.reload(false);
}
    useEffect(()=>{
        
        axios.get('http://localhost:3000/admin/courses', { 
      
        }).then((response)=>{
            setdata(response.data);
        })
    },[]);

   const deletedata=(id)=>{
    axios.delete(`http://localhost:3000/admin/course/${id}`)
    .then(res => {
        if(res.status ==200){
            deletok();
            
        }
 console.log(res.status)
    })
   }
    return(
        <div className="maincourses">

     {    data.map((dataa)=>
    <div className="coursescards">
              <img src="https://www.beytoote.com/images/stories/housekeeping/hou16389.jpg" className="coursesImage" />
<text style={{textAlign:'center'}}>{dataa.title}</text>
<div className="price">
 <text style={{color:'#009432'}}>تومان</text>
<text style={{color:'#009432'}}>{dataa.prices}</text>
 </div>  

 <div className="mainadmin" >
 <button className="adminwdit">ویرایش</button>
 <a className="admindlete"  onClick={()=>deletedata(dataa._id)}> حذف</a>
 </div>
 <button className="adminok" >تایید</button>
</div>

 ) }  
 
        </div>
    )
}
export default Courses;
