import React, { Component,useState } from 'react'
import './register.css';
import ReCAPTCHA from "react-google-recaptcha";
import { Link } from 'react-router-dom';
const Register=()=>{
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
   const [errors,setErrors]=useState('');
   const [recaptcha,setRecaptcha]=useState(false);
   const namesave=(event)=>{
    setName(event.target.value)
   }
   const emailsave=(event)=>{
    setEmail(event.target.value)
   }
   const passwordsave=(event)=>{
    setPassword(event.target.value)
   }
   const recaptchastate=()=>{
    setRecaptcha(true)
   }
   function datasave(){
     let err=[];
     if(name.length<1 || email.length<1){
err.push("لطفا همه فیلد ها را پر کنید");
setErrors(err)
     }else if(password.length<5){
       err.push("تعداد ارقام پسورد حداقل پنج رقم می باشد");
       setErrors(err)
     }else if(email.indexOf('@')== -1 || email.indexOf('.')== -1){
       err.push("فرمت ایمیل معتبر نمی باشد");
       setErrors(err)
     }else if(recaptcha == false){
      err.push("گزینه امنتی را فعال کنید");
      setErrors(err)
    }else{
      setErrors('')
      return(

        fetch('http://localhost:3000/auth/register',{
       
         
            method:'POST',
            headers:{
              'Accept':'application/json',
              // 'Content-type': 'application/json'
           
              // 'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
              'Content-Type': 'application/x-www-form-urlencoded'
            },
       
            body:JSON.stringify(
            {
        
                name:name,
                email:email,
                 password:password 
          
      
              }
             )
          
          })
          )
     }
 
   }
    return(
        <div className="mainregister">
<div className="error" style={{display:(err)}}>
{errors}
</div>
 
     <input type="text" placeholder="نام و نام خانوادگی" onChange={namesave} />

     <input type="email" placeholder="ایمیل"  onChange={emailsave}/>
    <input type="password" placeholder="رمز عبور" onChange={passwordsave}/>
    <text>
    {recaptcha}
    </text>
    <div className="recaptcha">
    <ReCAPTCHA
    hl={"fa"}
    sitekey="6LcdfOUUAAAAAFGXuwyP37TpZSyQpHHGxE28WGYo"
    onChange={recaptchastate}
  />,
 </div>
<button onClick={datasave}>
  ثبت نام
</button>

    <text>قبلا ثبت نام کرده اید؟<Link>وارد شوید</Link></text>

        </div>
    )
}
export default Register;
