import React, { Component,useState } from 'react'
import './register.css';
import { Link } from 'react-router-dom';
const Register=()=>{
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('')
   const namesave=(event)=>{
    setName(event.target.value)
   }
   const emailsave=(event)=>{
    setEmail(event.target.value)
   }
   const passwordsave=(event)=>{
    setPassword(event.target.value)
   }
   const datasave=()=>{
       return(
        fetch('http://localhost:3000/register',{

            method:'POST',
            headers:{
              'Accept':'application/json',
              'Content-Type':'application/json'
            },
            body:JSON.stringify(
              {
                "name":name,
                "email":email,
                "password":password
              }
             )
          
          })
        //   .then((response)=>{
        //     statusecod=response.status;
          
        //     if(statusecod === 200){
        //       sabtnamSuccsess(dispatch,navigation);
        //     }else{
        //       sabtnamFail(dispatch,navigation,response);
        //     }
        //   })
          .catch((error)=>{console.error(error)})
       )
   }
    return(
        <div className="mainregister">
       
     <input type="text" placeholder="نام و نام خانوادگی" onChange={namesave} />
     {/* <input type="number" placeholder="شماره همراه" /> */}
     <input type="email" placeholder="ایمیل"  onChange={emailsave}/>
    <input type="password" placeholder="رمز عبور" onChange={passwordsave}/>
    <input type="submit" value="ثبت نام" className="submitinput" onClick={datasave} />
    <text>قبلا ثبت نام کرده اید؟<Link>وارد شوید</Link></text>
        </div>
    )
}
export default Register;
