import React, { Component,useState } from 'react'
import './login.css';
import ReCAPTCHA from "react-google-recaptcha";
import { Link } from 'react-router-dom';

const Login=(props)=>{
    const [username, setusername] = useState('');
    const [password, setpassword] = useState('');
    const [errors, seterrors] = useState('');
    const [showerror,setshowerror]=useState('none')
    const [recaptcha,setRecaptcha]=useState(false);
    const pass=(event)=>{
        setpassword(event.target.value)
    }
    const mail=(event)=>{
        setusername(event.target.value)
    }
    const recaptchastate=()=>{
      setRecaptcha(true)
     }
     const gohome=()=>{
 
      props.history.push('/');
         }
    const login=(props)=>{
        let err=[];
        if(username.length<1 || password.length<1){
   err.push("لطفا همه فیلد ها را پر کنید");
   setshowerror('flex')
seterrors(err)
        }else if(password.length<5){
          err.push("تعداد ارقام پسورد حداقل پنج رقم می باشد");
          setshowerror('flex')
          seterrors(err)
        }else if(username.indexOf('@')== -1 || username.indexOf('.')== -1){
          err.push("فرمت ایمیل معتبر نمی باشد");
          setshowerror('flex')
          seterrors(err)
        }
        else if(recaptcha == false){
         err.push("گزینه امنتی را فعال کنید");
         seterrors(err)
       }
       else{
        seterrors('')
        setshowerror('none')
        return(
            fetch('http://localhost:3000/auth/login',{
                     method:'POST',
                     headers:{
                       'Accept':'application/json',
                       'Content-Type': 'application/x-www-form-urlencoded'
                     },
                     body:JSON.stringify(
                     {
                         email:username,
                          password:password 
                       }
                      )
                   })   .then(async (response)=>{
                     const data = await response.json();
                     const statuse=await response.status;
                   if(statuse == 200){
           
                  setshowerror('none')
                    console.log('kk66')
                   }else{
                    seterrors(data.message)
                    setshowerror('flex')
                   }
                   }
                  
                  )
                   .catch((error)=>{console.error(error)})
                   )
                }
    }
    return(
        <div className="mainlogin">
<div className="error" style={{display:errors.length<1?'none':'',}}>
{errors}
</div>
     <input type="email" placeholder="ایمیل" onChange={mail} />
    <input type="password" placeholder="رمز عبور" onChange={pass} />
 
    <div className="recaptcha">
    <ReCAPTCHA
    hl={"fa"}
    sitekey="6LcdfOUUAAAAAFGXuwyP37TpZSyQpHHGxE28WGYo"
    onChange={recaptchastate}
  />,
 </div>
<button onClick={login}>
  ورود
</button>

    <text>کاربر جدید هستید<Link>ثبت نام کنید</Link></text>

        </div>
    )
}
export default Login;
