import { IconContext } from "react-icons";
import { GoSearch  } from "react-icons/go";
import './searchbar.css';
const Searchbar=()=>{
    const[opacittytext,Setopacittytext]=useState(0.5)
   
   
        const logochang=()=>{
            if(opacittytext==0.5){
                Setopacittytext(1)
            }else{
                Setopacittytext(0.5)
            }
        }

        useEffect(() => {
            const interval = setInterval(() => {
             if(opacittytext==0.5){
                Setopacittytext(1)
             }
            }, 1000);
            return () => clearInterval(interval);
          }, []);
    return(
<div style={{marginTop:20,display:'flex'}}>
 
    <div className="searchBartext">
 <text>
     {opacittytext}
 </text>
    <text style={{fontSize:35,fontWeight:'bold',marginTop:0}}>Beh Kala</text>
    </div>

     <input type="text" className="input" placeholder="جستجوی محصولات"/> 

<div className="icon">
<IconContext.Provider

      value={{ color: '#F79F1F', size: '30px'}}>
 <GoSearch /> 
    </IconContext.Provider>
    </div>



   
   
 
     </div>
    )
}
 export default Searchbar;
