import React, { Component,useState } from 'react';
import './Modal.css';
import Backdrop from './../backdrop/backdrop';
import {Link} from 'react-router-dom';
const Modal=(props)=>{
    const [username, setusername] = useState('');
    const [password, setpassword] = useState('');
    const [errors, seterrors] = useState('');
    const [showerror,setshowerror]=useState('none')
    const pass=(event)=>{
        setpassword(event.target.value)
    }
    const mail=(event)=>{
        setusername(event.target.value)
    }
const close=()=>{
       props.closemodal();
    }

  
    const login=()=>{
        let err=[];
        if(username.length<1 || email.length<1){
   err.push("لطفا همه فیلد ها را پر کنید");
seterrors(err)
        }else if(password.length<5){
          err.push("تعداد ارقام پسورد حداقل پنج رقم می باشد");
          seterrors(err)
        }else if(email.indexOf('@')== -1 || email.indexOf('.')== -1){
          err.push("فرمت ایمیل معتبر نمی باشد");
          seterrors(err)
        }
    //     else if(recaptcha == false){
    //      err.push("گزینه امنتی را فعال کنید");
    //      seterrors(err)
    //    }
       else{
        seterrors(err)
        return(
            fetch('http://localhost:3000/auth/login',{
                     method:'POST',
                     headers:{
                       'Accept':'application/json',
                       'Content-Type': 'application/x-www-form-urlencoded'
                     },
                     body:JSON.stringify(
                     {
                         email:username,
                          password:password 
                       }
                      )
                   })   .then(async (response)=>{
                     const data = await response.json();
                     const statuse=await response.status;
                   if(statuse == 200){
               close()
                  setshowerror('none')
                    console.log('kk66')
                   }else{
                    seterrors(data.message)
                    setshowerror('flex')
                   }
                   }
                  
                  )
                   .catch((error)=>{console.error(error)})
                   )
                }
    }
    return(
        <React.Fragment>
            <Backdrop show={props.showModal} modalclose={props.closemodal}/>
      <div className="Modal"
        style={{transform:props.showModal?'translateX(0)':'translateX(-100vw)',
    opacity:props.showModal?'1':'0'
    }}
        >
 
         <input type="text" placeholder="username"  onChange={mail}/>
    <input type="password" placeholder="password" onChange={pass} />
    <div style={{display:'bluck',marginBottom:30,marginTop:15}}>
    <button onClick={login} style={{marginRight:30}}>
        ورود
    </button>
    <a onClick={()=>alert('kkk')} className="apassword">
       رمز عبور خود را فراموش کرده ام
    </a>
    </div>
     <div className="errorse" style={{display:showerror}}>
<text>{errors}</text>
     </div>
    <span style={{marginRight:15,color:'white'}}>کاربر جدید هستید؟
    <Link style={{color:'#F79F1F'}} onClick={props.closemodal}  to="/register" >ثبت نام</Link> کنید </span>
    </div>
 

        </React.Fragment>

  
    )
}
export default Modal;
