import React, { Component,useState } from 'react'
import './Createcomment.css'
const CreateComment=()=>{
    const [name, setname] = useState('')
    const [comment, setcomment] = useState('')
    const changename=(event)=>{
        setname(event.target.value)
    }
    return(
        <div className="maincomment">
        <div className="comment">
<text>ثبت نظر کاربران</text>
        </div>
        <input className="createcommentinput" placeholder="نام خود را وارد کنید" onChange={changename} />
        <textarea placeholder="دیدگاه خود را بنویسید" className="createcommenttextarea" onchange />
        <button className="createcommentbutton">ثبت نظر</button>
    <text>{name}</text>
        </div>
    )
}
export default CreateComment;
