        <button onClick={()=>nametown('بوشهر')} className="townbutton">بوشهر</button>
        <button onClick={()=>nametown('ایلام')} className="townbutton">ایلام</button>
        <button onClick={()=>nametown('تهران')} className="townbutton">تهران</button>
        <button onClick={()=>nametown('اهواز')} className="townbutton">اهواز</button>
        <button onClick={()=>nametown('بوشهر')} className="townbutton">بوشهر</button>
        <button onClick={()=>nametown('ایلام')} className="townbutton">ایلام</button>
        <button onClick={()=>nametown('تهران')} className="townbutton">تهران</button>
        <button onClick={()=>nametown('اهواز')} className="townbutton">اهواز</button>
        <button onClick={()=>nametown('بوشهر')} className="townbutton">بوشهر</button>
        </div>
    )
}
export default Towns;
