import { FaCaretUp,FaSortDown } from "react-icons/fa";
import Towns from './../../pages/towns/towns';
import City from './../citys/city';
import MapboxGeocoder from '@mapbox/mapbox-gl-geocoder';
import '@mapbox/mapbox-gl-geocoder/dist/mapbox-gl-geocoder.css';
var mapboxgl = require('mapbox-gl');
// var Layer = ReactMapboxGl.Layer;
// var Feature = ReactMapboxGl.Feature;


  
  
// import mapboxgl from 'mapbox-gl'
const Customerdetail=(props)=>{

    useEffect(() => {
 
    

        mapboxgl.accessToken = 'pk.eyJ1IjoiZGV2dGlhZ29mcmFuY2EiLCJhIjoiY2s4eGxvajIxMHpobjNsbWtiMzNhaXhsZCJ9.fGJuSvL9jVBPO8VD9huckA';
        var map = new mapboxgl.Map({
            container: 'map',
            style: 'mapbox://styles/mapbox/streets-v11',
            center: [-49.2712, -25.4296],
            zoom: 8
        });
    
        var marker = new mapboxgl.Marker({
            draggable: true
        })
            .setLngLat([12.550343, 55.665957])
            .addTo(map);
    
     map.on('click', function(e){
            console.log(e)
            var _lat = e.lngLat.lat;
            var _lng = e.lngLat.lng;
            // window.marker.setLngLat([_lng, _lat]);
            // window.onDragEnd();
        })
    
    }, [])
    const {customertowns,customercity,showtown,towns,showcity,citys}=useContext(mainContext)
   
    const [drop,setdrop]=useState('Dropdown');
    // const[showtown,setshowtown]=useState(false);
   const nametown=(text)=>{
    towns(text)
   }
   const namecity=(text)=>{
    citys(text)
   }
   
    return(
        <div>
      <div id="map"></div>
      <div id="geocoder" class="geocoder"></div>
      </div>
    )
}
export default Customerdetail;
