import React, { Component,useState,useContext } from 'react';
import './Modal.css';
import Backdrop from './../backdrop/backdrop';
import {Link} from 'react-router-dom';
import {withRouter} from 'react-router-dom';
import ReCAPTCHA from "react-google-recaptcha";
import {mainContext} from './../../context/mainContext';

const Modal=(props)=>{
  const {admin,modal,changemodal}=useContext(mainContext)
    const [username, setusername] = useState('');
    const [password, setpassword] = useState('');
    const [errors, seterrors] = useState('');
    const [showerror,setshowerror]=useState('none');
    const [recaptcha,setRecaptcha]=useState(false);
  const  closemodal=()=>{
    changemodal(false)
  }
    const recaptchastate=()=>{
      setRecaptcha(true)
     }
    const pass=(event)=>{
        setpassword(event.target.value)
    }
    const mail=(event)=>{
        setusername(event.target.value)
    }
const close=()=>{
       props.closemodal();
    }
    const gohome=()=>{
      props.history.push('/');
         }
         
        const reset=()=>{
          props.history.push('/reset');
          props.closemodal();
        }
    const login=()=>{
        let err=[];
        if(username.length<1 || password.length<1){
   err.push("لطفا همه فیلد ها را پر کنید");
   setshowerror('flex')
seterrors(err)
        }else if(password.length<5){
          err.push("تعداد ارقام پسورد حداقل پنج رقم می باشد");
          setshowerror('flex')
          seterrors(err)
        }else if(username.indexOf('@')== -1 || username.indexOf('.')== -1){
          err.push("فرمت ایمیل معتبر نمی باشد");
          setshowerror('flex')
          seterrors(err)
        }
        else if(recaptcha == false){
         err.push("گزینه امنتی را فعال کنید");
         setshowerror('flex')
         seterrors(err)
       }
       else{
        seterrors('')
        setshowerror('none')
        return(
            fetch('http://localhost:3000/auth/login',{
                     method:'POST',
                     headers:{
                       'Accept':'application/json',
                       'Content-Type': 'application/x-www-form-urlencoded'
                     },
                     body:JSON.stringify(
                     {
                         email:username,
                          password:password 
                       }
                      )
                   })   .then(async (response)=>{
                     const data = await response.json();
                     const statuse=await response.status;
                   if(statuse == 200){
                   gohome() 
               close()
                  setshowerror('none')
                    console.log('kk66')
                   }else{
                    seterrors(data.message)
                    setshowerror('flex')
                   }
                   }
                  
                  )
                   .catch((error)=>{console.error(error)})
                   )
                }
    }
    return(
        <React.Fragment>
  <div style={{dis}}  onClick={closemodal} className="Backdropmenu">

</div>
      <div className="Modal"
        style={{transform:modal?'translateX(0)':'translateX(-100vw)',
    opacity:modal?'1':'0'
    }}
        >
      <div className="errorse" style={{display:showerror}}>
<text>{errors}</text>
     </div>
         <input type="text" placeholder="username"  onChange={mail}/>
    <input type="password" placeholder="password" onChange={pass} />
    <ReCAPTCHA
    hl={"fa"}
    sitekey="6LcdfOUUAAAAAFGXuwyP37TpZSyQpHHGxE28WGYo"
    onChange={recaptchastate}
  />,

    <div style={{display:'bluck',marginBottom:30,marginTop:15}}>
    <button onClick={login} style={{marginRight:30}}>
        ورود
    </button>


    <Link onClick={reset} className="apassword">
       رمز عبور خود را فراموش کرده ام
    </Link>
    </div>


    <span style={{marginRight:15,color:'white'}}>کاربر جدید هستید؟
    <Link style={{color:'#F79F1F'}} onClick={props.closemodal}  to="/register" >ثبت نام</Link> کنید </span>
    </div>
 

        </React.Fragment>

  
    )
}
export default withRouter(Modal);
