        <a href="#">دوره ها</a>
      </div>
      <div className="createpost">
       <h1>ایجاد پست جدید</h1>
       <input type="text" placeholder="عنوان پست را وارد کنید" onChange={title}/>
       <input type="text" placeholder="قیمت را وارد کنید"  onChange={price} />
       <textarea  type="text" placeholder="توضیحات "    onChange={explain}>

       </textarea>
       <input type="file" name="انتخاب فایل" style={{backgroundColor:'white'}}  />

       <button onClick>
         ثبت اطلاعات
       </button>
 
       </div>
      
      
      </div>
    )
}
export default Adminmain;
