    <text>
        آدرس:ایلام چرداول آسمان آباد روستای بابا مرادی علیا
    </text>
</div>
<text>مبلغ:453000000</text>
<div className="mainbutton">
<button className="payeditbutton" onClick={()=>props.history.push('/EditCustomerdetail')}>ویرایش جزییات</button>
<button className="paybutton">پرداخت</button>
</div>

</div>
            </div>
        </div>
    )
}
export default Pay;
