import React, { Component,useContext } from 'react'
import './slideDrowerpages.css';
import Flowerlist from './pages/flowerlist';
import { IconContext } from "react-icons";
import {mainContext} from './../../../context/mainContext';
 const SlideDrowerpages=()=>{
    const {menu,changemenu,menuname,changemenuname}=useContext(mainContext)
    const closemenu=()=>{
        changemenu(!menu) 
      }
    return(
         <div >
    
      <div style={{position:'sticky',top:0,right:0,width:100,height}} >
      <IconContext.Provider
      value={{ color: 'red', size: '25px'}}>
 <IoMdMenu onClick={()=>closemenu()} /> 
    </IconContext.Provider>
       </div>
         <div style={{display:menuname=='flower'?'flex':'none'}}>
        <Flowerlist/>
         </div>
         </div>
     )
 }
 export default SlideDrowerpages;
