      </div>
 
      {/* <form className="createpost"  action="http://localhost:3000/admin/course/create" method="POST" enctype="multipart/form-data"> */}
      <div  className="createpost">

  
       <h1>ایجاد پست جدید</h1>
       <div className="error" style={{display:errors.length<1?'none':'flex'}}>
       <text > {errors}</text>
       </div>
     
      
       <input type="text" placeholder="عنوان پست را وارد کنید" className="inputs" onChange={title}/>
       <input type="text" placeholder="قیمت را وارد کنید" className="inputs" onChange={price} name="price"  />
       <textarea  type="text" placeholder="توضیحات "  className="inputs" onChange={explain} name="explain" >

       </textarea>
       <div  className="inputselect">
       
       <select onChange={select} className="selectbox">
    <option value="0">نوع</option>
    <option value="1">Audi</option>
    <option value="2">BMW</option>
  </select>
  <input type="file" name="file" className="file" onChange={selectfile} />
       </div>
  
       <button onClick={store}>
         ثبت اطلاعات
       </button>
       {/* </form> */}
       </div>
      
      
      </div>
    )
}
export default Adminmain;
