import React, { Component,useState,useContext } from 'react';
import {  IoIosArrowForward } from "react-icons/io";
import { IconContext } from "react-icons";
import {Link} from 'react-router-dom';
import './sideDrawer.css';
import Logo from './../logo/logo';
 import Backdrop from '../backdrop/backdrop';
 import SlideDrowerpages from './slideDrowerpages/slideDrowerpages';
 import {mainContext} from './../../context/mainContext';
const SideDrawer=(props)=>{
    const {menu,changemenu,drawer}=useContext(mainContext)
   const changeDrawer=()=>{
      
   props.closeDrawer()
   }
    let classes=['SideDrawer','Close'];
    if(props.show){
        classes=['SideDrawer','open']
    }
    if(drawer==false){
        classes=['SideDrawer','o']
    }
    pen
    let classes2=['SideDrawer2','Close2'];
    if(menu==true){
        classes2=['SideDrawer2','open2']
    }
   
  const  openmenu=()=>{
    changemenu(!menu)
  }
  const closemenue=()=>{
    changemenu(!menu) 
  }
    return(
        <React.Fragment>
 <Backdrop show={props.show} modalclose={props.closeDrawer}/>

<div style={{display:menu ?'none':'block'}}  className={classes.join(' ')} style={{paddingTop:0}} >
    <div >
  
    <Logo/> 
    </div>
    
  
    
    <Link style={{display:menu ?'none':'block'}}  className="Draweritems" onClick={changeDrawer} to="/">صفحه اصلی</Link>
       <Link style={{display:menu ?'none':'block'}}  className="Draweritems" onClick={changeDrawer} to="/flower">گل</Link>
       <button onClick={openmenu}>
           <text>hho</text>
       </button>
       <div className={classes2.join(' ')}>
           <div >
           <button onClick={closemenue}>
             close
           </button>
           </div>
           <SlideDrowerpages  />
       </div>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" >لوتزم خانگی</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" >آرایشی و بهداشتی</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems"  onClick={changeDrawer} to="/Computer" > کامپیوتر و لپ تاپ</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" >لوازم جانبی دیجیتال</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" onClick={changeDrawer} to="/flower">گل</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" >لوتزم خانگی</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" >آرایشی و بهداشتی</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems"  onClick={changeDrawer} to="/Computer" > کامپیوتر و لپ تاپ</Link>
       <Link style={{display:menu ?'none':'block'}} className="Draweritems" >لوازم جانبی دیجیتال</Link>
   
   </div>
   
        </React.Fragment>
        
    )
}
export default SideDrawer;
