import React, { Component,useState,useContext} from 'react';
import { IconContext } from "react-icons";
import MenuItems from '../../components/MenuItems/MenuItems';
import SideDrawer from '../sideDrawer/sideDrawer';
import './Toolbar.css';
import { FaPhone } from "react-icons/fa";
import { IoMdMenu,IoMdPerson } from "react-icons/io";
import {MdShoppingCart } from "react-icons/md";
import Modal from '../Modal/Modal';
import {mainContext} from './../../context/mainContext';
import { Link } from 'react-router-dom';
const Toolbar=(props)=>{
    const {menu,changemenu,drawer,changedrawer,login}=useContext(mainContext)
    const[show,setShow]=useState(false);
    const[modalshow,setShowmodal]=useState(false)
   const closemodal=()=>{
    setShowmodal(false)
    }
    const closeDrawer=()=>{
        setShow(false)
    }
    const opensidDrawer=()=>{
        // setShow(true)
changedrawer()

    }
    return(
<div className="Toolbar">

<SideDrawer  show={show} closeDrawer={closeDrawer}  />

<div className="sizemin" >

<span  >

   <IconContext.Provider
      value={{ color: 'red', size: '25px'}}>
 <IoMdMenu  onClick={opensidDrawer} /> 
    </IconContext.Provider>
      </span>
    
{/* <MenuItems/> */}
</div>
<div className="customermanage">
<Link   style={{display:login?'flex':'none'}} className="modallogin" to="/user/buys">
       <IconContext.Provider
      value={{ color: '#ffffff', size: '25px' }}>
      <span > <MdShoppingCart  /></span>
    </IconContext.Provider>
           <span style={{}}>سبدخرید</span>
           
       </Link>

{/* ------------------------------- */}
       <Link  style={{display:login?'flex':'none'}} className="buyscustomer" to="/Customerpage">
       <IconContext.Provider
      value={{ color: '#ffffff', size: '25px' }}>
      <span > <IoMdPerson /></span>
    </IconContext.Provider>
           <span >حساب کاربری من</span>
           
       </Link>
{/* ------------------------- */}
       <Link   style={{display:login?'none':'flex'}} className="buyscustomer"  >
       <IconContext.Provider
      value={{ color: '#ffffff', size: '25px' }}>
      <span > <IoMdPerson /></span>
    </IconContext.Provider>
           <span>ثبت نام ورود</span>
           
       </Link>
       </div>
<span className="showNav">

            <nav >
             {/* <MenuItems openmodal={()=>setShowmodal(true)} /> */}
          
         </nav>
        
            </span>
           
            <span style={{color:'#ffffff',height:'100%',display:'flex',alignItems:'center'}}>
      <span style={{marginLeft:3,marginTop:10}} >
     <div className="phone">
   <IconContext.Provider
      value={{ color: '#F79F1F', size: '18px'}}>
 <FaPhone /> 

    </IconContext.Provider>
    </div>
      </span>
      <span style={{marginLeft:4,marginTop:6,color:'#F79F1F'}} className="phone">
      09106861071
      </span>
      <span className="showNav">
      پشتیبانی و فروش  
      </span>
    
            </span>
            <Modal showModal={modalshow}  closemodal={()=>setShowmodal(false)}/>
</div>
          
           

    )
}
export default Toolbar;
