import React, { Component } from 'react'
import './customerpage.css'
import { IconContext } from "react-icons";
import { AiOutlinePoweroff } from "react-icons/ai";
import {FaKey,FaUserEdit, FaBuysellads } from "react-icons/fa";
import {IoIosBasket } from "react-icons/io";
import {MdShoppingCart } from "react-icons/md";
import { Link } from 'react-router-dom';
import Customerbuys from '../buys/customerbuys';
const Customerpage=(props)=>{
      const logout=()=>{
            localStorage.removeItem('token')
            window.location.reload()
      }
      const newbuy=()=>{
            props.history.push('/user/buys');
      }
     const Customerbuys=()=>{
      props.history.push('/Customerbuys');
     }
     const edit=()=>{
      props.history.push('/editprofile');
     }
    return(
          
            <div className="main"> 


<button onClick={()=>logout()} className="logout">
            <IconContext.Provider
      value={{  size: '35px' }}>
 < AiOutlinePoweroff />
      </IconContext.Provider>
           
            
           
            <span className="title">خروج</span>
            </button>
          
            
            
            <div className="logout">
            <IconContext.Provider
      value={{  size: '35px' }}>
 < FaKey />
      </IconContext.Provider>
      <Link to={'./reset'}>فراموشی رمز عبور</Link>
            <button className="title">تغییر رمز عبور</button>
            </div>

            {/* <div className="logout">
            <IconContext.Provider
      value={{  size: '40px' }}>
   < IoIosBasket />
      </IconContext.Provider>
          
            <span>کالاهای خریداری شده</span>
            </div> */}
            <button oClick={()=>Customerbuys()}n  className="logout">
            <IconContext.Provider
      value={{  size: '35px' }}>
   < IoIosBasket />
      </IconContext.Provider>
          
            <span className="title">کالاهای خریداری شده</span>
            </button>
            <button onClick={()=>newbuy()} className="logout">
            <IconContext.Provider
      value={{  size: '35px' }}>
   <  MdShoppingCart />
      </IconContext.Provider>
          
            <span className="title">سبد خرید</span>
            </button>
            <button onClick={()=>edit()} className="logout">
            <IconContext.Provider
      value={{  size: '35px' }}>
   <  FaUserEdit />
      </IconContext.Provider>
          
            <span className="title">ویرایش اطلاعات کاربری</span>
            </button >
            </div>
       
    )
}
export default Customerpage;
